import React, {useState, useEffect} from "react";
// import Athlete from "./Athlete";
import AthleteDetails from "./AthleteDetails";
import AthleteList from "./AthleteList";
// import data from "./props.json";

const sports = [
    "Baseball",
    "Softball",
    "Gymnastics",
    "Soccer",
    "Wrestling",
    "Football",
    "Lacrosse",
    "Hockey"
]

const AthleteSelector = () => {
    const [sport, setSport] = useState("");
    const [athlete, setAthlete] = useState("")
    const [athletes] = AthleteList(sport)

    useEffect(() => {
        requestAthletes();
    }, [])

    async function requestAthletes() {
        const res = await fetch(
            `http://localhost:3030/athletes?sport=${sport}`
        );
        const json = await res.json();
        setAthlete(json.athletes)
        console.log(json)
    }



    const handleChange = (e) => {
        setSport(e.target.value);
        requestAthletes();
        }

    return (
        <div className="selector">
            <form>
            <select 
                id="sport"
                value={sport}
                onChange={(e) => {
                    setSport(e.target.value)
                }}>
                <option/>
                {sports.map((sport) => {
                    return (
                        <option key={sport} value={sport}>
                            {sport}
                        </option>
                    ) 
                })}
            </select>
            <select 
                id="athlete"
                value={athlete}
                onChange={(e) => {
                setAthlete(e.target.value)
                }}>
    <option/>
        {athletes.map((athlete) => {
            return (
                <option key={athlete} value={athlete}>{athlete}</option>
            )})
        }
          </select>
            <h3>you selected: {sport}</h3>
            <AthleteDetails />

        </form>
        </div>
    )
}


export default AthleteSelector
