import React, {useState, useEffect} from "react";
import AthleteDetails from "./AthleteDetails";
import AthleteList from "./AthleteList";

const sports = [
    "baseball",
    "softball",
    "gymnastics",
    "soccer",
    "wrestling",
    "football",
    "lacrosse",
    "hockey"
]

const AthleteSelector = () => {
    const [sport, setSport] = useState("");
    const [athlete, setAthlete] = useState([])
    const [athletes] = AthleteList(sport)

    useEffect(() => {
        requestAthletes();
        console.log(ath)
    },[sport])

async function requestAthletes() {
        const res = await fetch(
            `http://localhost:3030/athletes?sport=${sport}`
        );
        const json = await res.json();
        setAthlete(json)
        console.log(json)
    }

    // const handleChange = (e) => {
    //     setSport(e.target.value);
    //     requestAthletes();
    //     }

    return (
        <div className="selector">
            <form
            onSubmit={(e) => {
                e.preventDefault();
                requestAthletes();
            }}>
            <select 
                id="sport"
                value={sport}
                onChange={(e) => {
                    setSport(e.target.value)
                }}>
                <option/>
                {sports.map((sport) => {
                    return (
                        <option key={sport} value={sport}>
                            {sport}
                        </option>
                    ) 
                })}
            </select>
            <select 
                id="athlete"
                value={athlete}
                onChange={(e) => {
                setAthlete(e.target.value)
                }}>
        <option/>
        {athlete.map((athlete) => {
            return (
                <option key={athlete.id} value={athlete.name}>{athlete.name}</option>
            )})
        }
          </select>
            <h3>you selected: {athlete.map((ath) => {
                return (
                    <h1 id={ath.id}>{ath.name}</h1>
                )
            })}</h3>
            <button>Submit</button>
        </form>
        <AthleteDetails name={athlete.name} sport={athlete.sport} key={athlete.id}/>
        </div>
    )
}


export default AthleteSelector
