import React, {useState, useEffect} from "react";
import Athlete from "./Athlete";
// import data from "./props.json";

const AthleteSelector = () => {
    const [options, setOptions] = useState();
    const [athlete, setAthlete] = useState([]);

    useEffect(() => {
        requestAthletes();
    }, [options])

    async function requestAthletes() {
        const res = await fetch(
            `http://localhost:3030/athletes?sport=${options}`
        );
        const json = await res.json();
        console.log(json)
        setAthlete(json.athlete)
    }

    const sports = [
        "Baseball",
        "Softball",
        "Gymnastics",
        "Soccer",
        "Wrestling",
        "Football",
        "Lacrosse",
        "Hockey"
    ]

    const handleChange = (e) => {
        setOptions(e.target.value);
        requestAthletes();
        }

    const filter = (e) => {
        setAthlete(e.target.value)
    }

    return (
        <div className="selector">
            <select onChange={handleChange}>
                <option>Select a Sport</option>
                {sports.map((sport, index) => {
                    return (
                        <option key={index} value={sport}>
                            {sport}
                        </option>
                    )

                })}
            </select>
            <h3>you selected: {options}</h3>

            {
                athlete.map((athlete) => (
                    <Athlete name={athlete.name} date={athlete.date} sport={athlete.sport} key={athlete.id} />
                ))
            }
        </div>
    )
}


export default AthleteSelector
