import React, {useState, useEffect} from "react";
import Athlete from "./Athlete";
import AthleteDetails from "./AthleteDetails";
import AthleteList from "./AthleteList";
// import data from "./props.json";

const sports = [
    "Baseball",
    "Softball",
    "Gymnastics",
    "Soccer",
    "Wrestling",
    "Football",
    "Lacrosse",
    "Hockey"
]

const AthleteSelector = () => {
    const [sport, setSport] = useState();
    const [athlete] = AthleteList()

    useEffect(() => {
        requestAthletes();
    }, [sport])

    async function requestAthletes(sport) {
        const res = await fetch(
            `http://localhost:3030/athletes?sport=${sport}`
        );
        const json = await res.json();
        console.log(json)
        setAthlete(json.athletes)
    }



    const handleChange = (e) => {
        setSport(e.target.value);
        requestAthletes();
        }

    return (
        <div className="selector">
            <select onChange={handleChange}>
                <option>Select a Sport</option>
                {sports.map((sport) => {
                    return (
                        <option key={sport} value={sport}>
                            {sport}
                        </option>
                    )

                })}
            </select>
            <h3>you selected: {sport}</h3>
            <AthleteDetails />
        </div>
    )
}


export default AthleteSelector
