import React, {useState, useEffect} from "react";
import Results from "./Results";
import AthleteList from "./AthleteList";
import AvgSleep from "./AvgSleep";

const sports = [
    "baseball",
    "softball",
    "gymnastics",
    "soccer",
    "wrestling",
    "football",
    "lacrosse",
    "hockey",
    "track"
]

const AthleteSelector = () => {
    const [sport, setSport] = useState("");
    const [athletes, setAthletes] = useState([])
    const [individualAthlete, setIndividualAthlete] = useState('');

    //AthleteList(sport)

    // useEffect(() => {
    //     requestAthletes();
    //     console.log(sport)
    // },[sport])

async function requestAthletes() {
        const res = await fetch(
            `http://localhost:3030/athletes?sport=${sport}`
        );
        const json = await res.json();
        setAthletes(json)
        console.log(json)
    }

    // const handleChange = (e) => {
    //     setSport(e.target.value);
    //     requestAthletes();
    //     }

    return (
        <div className="selector">
            <form
            onSubmit={(e) => {
                e.preventDefault();
                requestAthletes();
            }}>
            <select 
                id="sport"
                value={sport}
                onChange={(e) => {
                    setSport(e.target.value)
                }}>
                <option/>
                {sports.map((sport) => {
                    return (
                        <option key={sport} value={sport}>
                            {sport}
                        </option>
                    ) 
                })}
            </select>
            <select 
                id="athlete"
                value={individualAthlete}
                onChange={(e) => {
                setIndividualAthlete(e.target.value)
                }}>
        <option/>
        {athletes.map((athlete) => {
            return (
                <option key={athlete.id} value={athlete.name}>{athlete.name}</option>
            )})
        }
          </select>
            <h3>you selected: {athlete.map((ath) => {
                return (
                    <h1 id={ath.id}>{ath.name}</h1>
                )
            })}</h3>
            <button>Submit</button>
        </form>
        <Results athlete={individualAthlete}/>
        <AvgSleep athlete={individualAthlete}/>
        </div>
    )
}


export default AthleteSelector
